package ex02연산자;

public class Ex06비교연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 10;
		int num2 = 5;
		int num3 = 10;
		int num4 = 5;
		// 비교연산자의 결과값은 항상 true 아니면 false로 나온다
		// boolean
		System.out.println(num1 > num2);
		System.out.println(num1 < num2);
		System.out.println(num1 == num3); // 같은 수 비교 -> ==
		System.out.println(num1 != num3); // != : 같지않다
		System.out.println(num1 >= num3); // >= : 크거나 같다
		
		
	}

}
