package ex03for;

public class Ex01for {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// for - 반복 횟수가 주로 정해졌을 때 사용되는 반복문
		// for(초기화구문(무조건 한 번만 실행); 검사조건; 반복후작업) {
		//		반복을 할 부분
		// }
		// for문을 사용하여 1부터 5까지 출력
		for(int i = 1; i <= 5; i++) {
			System.out.println(i);
		}
		
		
			
	}

}
